import './comment.css';
import Likecount from './UI/likecount';
import Avatar2 from './UI/Avatar2';
import Header from './UI/header';
import Text from './UI/commentText';



const Comment = () => {

    const amyrobson = 'Impressive! Though it seems the drag feature could be improved. But overall it looks incredible. You`ve nailed the design and the responsiveness at various breakpoints works really well.'

    return(
        <div className="comment">
            <div className="rest">
                <Likecount/>
                <Avatar2 avatar2={require('./UI/avatars/image-amyrobson.png')}/>
                <Header/>     
            </div>

            <div className="content">
                <Text class="commentText" commentText={amyrobson}/>
            </div>
        </div>
    )
}
export default Comment;
