import './App.css';
import NewComment from './components/newComment'
import Comment from './components/comment'
const avatar2 = require('./components/UI/avatars/image-amyrobson.png')
const amyinput = 'Impressive! Though it seems the drag feature could be improved. But overall it looks incredible. You`ve nailed the design and the responsiveness at various breakpoints works really well.'


const App = () => {

  const addNewComment = () => {
    return <Comment/>
  }



  return(
    <div>
      <Comment avatar2={avatar2} input={amyinput} username={'amyrobson'}/>
      {
        addNewComment
      }
      <NewComment/>
    </div>
  )

}

export default App;
